package Draw2021;import java.io.*;import java.util.*;import java.awt.*;import java.awt.geom.*;import javax.swing.*;/*Version History*/abstract class DrawEngine extends JFrame {		public DrawEngine(String title) {		super(title);add(new MyPanel());		setSize(600,600);		setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		setLocationRelativeTo(null);		this.getContentPane().setBackground(Color.WHITE);	}		abstract public void drawObjects(Graphics2D g2d);		public void paint(Graphics g) {		super.paint(g);	}	private class MyPanel extends JPanel {		public void paint(Graphics g) {			super.paint(g);			drawObjects((Graphics2D) g);		}	}}